const Product = (props) => {
    const { id, title, price, category, images } = props.data;
    return (
        <div className="col-sm-3 mb-3">
            <div className="card h-100 shadow-sm">
                <img src={images?.[0]} alt={title} className="card-img-top rounded" />
                <div className="card-body">
                    <h5 className="card-title">{title}</h5>

                    <p className="card-text">
                        <span className="badge bg-secondary">{category?.name}</span>
                    </p>

                    <h2 className="text-primary">
                        <span>&#8377;</span> {price}
                    </h2>

                    <a href="#" className="btn btn-primary d-block w-100">
                        Show Details
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Product;
